/* global describe, it */

'use strict';

var assert     = require('expressive-assertion');
var statistics = require('../lib/statistics');

describe('statistics', function () {
    describe('.mean()', function () {
        it('returns the mean of the given numerical data', function () {
            assert(function () {
                return isNaN(statistics.mean([]));
            }, function () {
                return statistics.mean([-1]) === -1;
            }, function () {
                return statistics.mean([-1, 2, 3, 4, 4]) === 2.4;
            }, function () {
                return statistics.mean([-1, 2.5, 3.25, 5.75]) === 2.625;
            });
        });
    });

    describe('.median()', function () {
        it('returns the median of the given numerical data', function () {
            assert(function () {
                return isNaN(statistics.median([]));
            }, function () {
                return statistics.median([-1]) === -1;
            }, function () {
                return statistics.median([-1, 3, 5]) === 3;
            }, function () {
                return statistics.median([-1, 3, 5, 7]) === 4;
            }, function () {
                return statistics.median([-1, 7, 3, 5, 4]) === 4;
            });
        });
    });
});
